SELECT city.city_id,city.city,customer.first_name,customer.last_name,customer.email,address.address FROM sakila.customer join sakila.address on address.address_id = customer.address_id join sakila.city on  address.city_id = city.city_id where city.city_id=312;
SELECT film.film_id, film.title, film.description, film.release_year, film.rating, film.special_features, category.name from sakila.film join sakila.film_category ON film.film_id=film_category.film_id JOIN sakila.category on category.category_id= film_category.category_id WHERE category.name = 'Comedy';
SELECT actor.actor_id, concat(actor.first_name, " " , last_name) AS actor_name, film_actor.film_id, film.title, film.description, film.release_year FROM sakila.actor JOIN sakila.film_actor ON actor.actor_id = film_actor.actor_id JOIN sakila.film ON film.film_id = film_actor.film_id WHERE actor.actor_id = 5;
SELECT store.store_id,address.city_id,customer.first_name,customer.last_name,customer.email,address.address FROM sakila.customer JOIN sakila.address ON customer.address_id = address.address_id LEFT JOIN sakila.store ON store.store_id = customer.store_id WHERE store.store_id = 1 AND (address.city_id = 1 OR address.city_id = 42 OR address.city_id = 312 OR address.city_id= 459);
SELECT film.title,film.description,film.release_year,film.rating,film.special_features FROM sakila.film JOIN sakila.film_actor ON film.film_id = film_actor.film_id WHERE film.rating = 'G' AND film.special_features LIKE '%Behind the Scenes%' AND film_actor.actor_id = 15;
SELECT film_actor.film_id, film.title,film_actor.actor_id,concat(actor.first_name, '  ', actor.last_name) AS actor_name FROM sakila.film_actor JOIN sakila.film ON film_actor.film_id = film.film_id  JOIN sakila.actor ON actor.actor_id = film_actor.actor_id WHERE film.film_id = 369;
SELECT film.film_id,film.title,film.description,film.release_year,film.rating,film.special_features,category.name AS genre,film.rental_rate FROM sakila.film JOIN sakila.film_category ON film_category.film_id = film.film_id JOIN sakila.category ON category.category_id = film_category.category_id WHERE category.name = 'Drama' AND film.rental_rate = 2.99;
SELECT actor.actor_id,concat(actor.first_name,'  ', actor.last_name) AS actor_name, film.film_id,film.title,film.description,film.release_year,film.rating,film.special_features,category.name AS genre FROM sakila.film JOIN sakila.film_category ON film_category.film_id = film.film_id JOIN sakila.category ON category.category_id = film_category.category_id JOIN sakila.film_actor ON film_actor.film_id = film.film_id JOIN sakila.actor ON film_actor.actor_id = actor.actor_id WHERE concat(actor.first_name,' ', actor.last_name) = 'SANDRA KILMER';